import React, { useState } from 'react'
import axios from 'axios'

const QuotesContext = React.createContext()

function QuotesContextProvider(props) {

    const [mode, setMode] = useState("light")
    const [confidence, setConfidence] = useState([])
    const [inspiration, setInspiration] = useState([])
    const [kindness, setKindness] = useState([])
    const [success, setSuccess] = useState([])
    const [today, setToday] = useState([])

    function getConfidence() {
        axios.get(`https://vschool-cors.herokuapp.com?url=https://zenquotes.io/api/quotes/&keyword=confidence`, {
            params: {
                _limit: 10
            }
        })
            .then(res => setConfidence(prevList => ([...prevList, res.data])))
            .catch(err => console.log(err))
    }

    function getInspiration() {
        axios.get(`https://vschool-cors.herokuapp.com?url=https://zenquotes.io/api/quotes/&keyword=inspiration`, {
            params: {
                _limit: 10
            }
        })
            .then(res => setInspiration(prevList => ([...prevList, res.data])))
            .catch(err => console.log(err))
    }

    function getKindness() {
        axios.get(`https://vschool-cors.herokuapp.com?url=https://zenquotes.io/api/quotes/&keyword=kindness`, {
            params: {
                _limit: 10
            }
        })
            .then(res => setKindness(prevList => ([...prevList, res.data])))
            .catch(err => console.log(err))
    }

    function getSuccess() {
        axios.get(`https://vschool-cors.herokuapp.com?url=https://zenquotes.io/api/quotes/&keyword=success`, {
            params: {
                _limit: 10
            }
        })
            .then(res => setSuccess(prevList => ([...prevList, res.data])))
            .catch(err => console.log(err))
    }

    function getToday() {
        axios.get(`https://zenquotes.io/api/quotes/&keyword=success`)
            .then(res => setToday(prevList => ([...prevList, res.data])))
            .catch(err => console.log(err))
    }

    function changeMode(){
        setMode(prevMode => prevMode === "light" ? "dark" : "light")
    }

    return (
        <QuotesContext.Provider value={{ 
            confidence, 
            inspiration, 
            kindness, 
            success, 
            today,
            mode,
            getConfidence,
            getKindness,
            getInspiration,
            getSuccess,
            getToday,
            changeMode
            }}>
            { props.children }
        </QuotesContext.Provider>
    )
}

export { QuotesContextProvider, QuotesContext }
