import React, { useState, useEffect } from 'react'
import axios from 'axios'

const QuotesContext = React.createContext()

const confidence = axios.create({
    api_url: "https://zenquotes.io/api/quotes/&keyword=confidence"
})

const inspiration = axios.create({
    api_url: "https://zenquotes.io/api/quotes/&keyword=inspiration"
})

const kindness = axios.create({
    api_url: "https://zenquotes.io/api/quotes/&keyword=kindness"
})

const success = axios.create({
    api_url: "https://zenquotes.io/api/quotes/&keyword=success"
})

const clientToday = axios.create({
    api_url: "https://zenquotes.io/api/today/"
})

function QuotesContextProvider(props) {

    const [confidence, setConfidence] = useState([])
    const [inspiration, setInspiration] = useState([])
    const [kindness, setKindness] = useState([])
    const [success, setSuccess] = useState([])
    const [today, setToday] = useState([])

    function getConfidence() {
        axios.get(`https://zenquotes.io/api/quotes/&keyword=confidence`, {
            params: {
                _limit: 10
            }
        })
            .then(res => setConfidence(prevList => ([...prevList, res.data])))
            .catch(err => console.log(err))
    }

    function getInspiration() {
        axios.get(`https://zenquotes.io/api/quotes/&keyword=inspiration`, {
            params: {
                _limit: 10
            }
        })
            .then(res => setInspiration(prevList => ([...prevList, res.data])))
            .catch(err => console.log(err))
    }

    function getKindness() {
        axios.get(`https://zenquotes.io/api/quotes/&keyword=kindness`, {
            params: {
                _limit: 10
            }
        })
            .then(res => setKindness(prevList => ([...prevList, res.data])))
            .catch(err => console.log(err))
    }

    function getSuccess() {
        axios.get(`https://zenquotes.io/api/quotes/&keyword=success`, {
            params: {
                _limit: 10
            }
        })
            .then(res => setSuccess(prevList => ([...prevList, res.data])))
            .catch(err => console.log(err))
    }

    useEffect(() => {
        clientToday
            .get('?_limit=10')
            .then
    }, [])

    return (
        <QuotesContext.Provider value={{ 
            confidence, 
            inspiration, 
            kindness, 
            success, 
            today,
            getConfidence,
            getKindness,
            get
            }}>
            { props.children }
        </QuotesContext.Provider>
    )
}

export { QuotesContextProvider, QuotesContext }
