import React, { useState } from 'react'
import axios from 'axios'

const QuotesContext = React.createContext()



function QuotesContextProvider(props) {

   const secret = process.env.YOUR_APP_TOKEN

    const [confidence, setConfidence] = useState([])
    const [inspiration, setInspiration] = useState([])
    const [kindness, setKindness] = useState([])
    const [success, setSuccess] = useState([])
    const [today, setToday] = useState({
        q: "",
        a: ""
    })
    //const [today, setToday] = useState([])

    function getConfidence() {
        axios.get(`https://favqs.com/api/quotes/?filter=confidence`, {
            header: {
                'Authorization' : `Tokenoken=${secret}`

            }
        })
            .then(res => setConfidence(res.data))
            .catch(err => console.log(err))
    }

    function getInspiration() {
        axios.get(`https://vschool-cors.herokuapp.com?url=https://type.fit/api/quotes`)
            .then(res => setInspiration(res.data))
            .catch(err => console.log(err))
    }

    function getKindness() {
        axios.get(`https://vschool-cors.herokuapp.com?url=https://zenquotes.io/api/quotes/&keyword=kindness`, {
            params: {
                _limit: 10
            }
        })
            .then(res => setKindness(prevList => ([...prevList, res.data])))
            .catch(err => console.log(err))
    }

    function getSuccess() {
        axios.get(`https://vschool-cors.herokuapp.com?url=https://zenquotes.io/api/quotes/&keyword=success`, {
            params: {
                _limit: 10
            }
        })
            .then(res => setSuccess(prevList => ([...prevList, res.data])))
            .catch(err => console.log(err))
    }

    function getToday() {
        axios.get(`https://vschool-cors.herokuapp.com?url=https://zenquotes.io/api/random/?option1=q&option2=a`)
            .then(res => setToday({
                q: res.data[0].q,
                a: res.data[0].a
            }))
            .catch(err => console.log(err))
    }

    return (
        <QuotesContext.Provider value={{ 
            confidence, 
            inspiration, 
            kindness, 
            success, 
            today,
            getConfidence,
            getKindness,
            getInspiration,
            getSuccess,
            getToday
            }}>
            { props.children }
        </QuotesContext.Provider>
    )
}

export { QuotesContextProvider, QuotesContext }
