    const {kindness, getKindness} = useContext(QuotesContext)

    useEffect(() => {
        getKindness()
    }, [])

    return (
        <div className='kindness'>
            <h2>Quotes of Kindness from Inspirational Leaders</h2>
            <p className='kindness-quotes'>

            </p>
        </div>
    )
}
